package com.dm.basic;

public class Eg3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//wrappers type /wrappers classes	
		
		//Numbers
				Byte b = 10;
				System.out.println(b);
				
				Short s = 20;
				System.out.println(s);
				
				Integer i = 30;
				System.out.println(i);
				
				Long l = 40L;
				System.out.println(l);
				
				//floating points
				Float f = 50.0f;
				System.out.println(f);
				
				Double d = 60.0d;
				System.out.println(d);
				
				//characters
				Character ch = 'a';
				System.out.println(ch);
				
				//boolean
				boolean b1= true;
				System.out.println(b1);
				
				//string 
				String s1 = "aabc";
				System.out.println(s1);
				

	}

}

//class  class is a blue print or template
//In class we have properties, methods,blocks,constructors
//Two types of classes
//Build In class : String, Arrays etc...
//User Defined class : Eg1,Eg2, etc...